catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   // Get the value of the First Day of Leave
    var firstDayOfLeave = g_form.getValue('first_date_of_leave');

    if (firstDayOfLeave === '') {
        // If the First Day of Leave is not set, show an error message
        //g_form.showFieldMsg('first_date_of_leave', 'First day of leave cannot be empty', 'error');
        g_form.addErrorMessage('First day of leave cannot be empty');

        // Optionally, clear the Last Day of Leave field
        g_form.setValue('last_day_of_leave', '');

        return;
    } else {
        // Clear any existing error messages specific to the field
        g_form.hideFieldMsg('first_date_of_leave');
        g_form.clearMessages();
    }

    // Convert the values to Date objects
    var firstDayDate = new Date(firstDayOfLeave);
    var lastDayDate = new Date(newValue);

    // Set both dates to midnight to compare dates only
    firstDayDate.setHours(0, 0, 0, 0);
    lastDayDate.setHours(0, 0, 0, 0);

    // Check if the Last Day of Leave is before the First Day of Leave
    if (lastDayDate < firstDayDate) {
        // Show an error message
        g_form.showFieldMsg('last_day_of_leave', 'Last day of leave must be after the first day of leave', 'error');
        g_form.addErrorMessage('Last day of leave must be after the first day of leave');

        // Optionally, clear the Last Day of Leave field
        g_form.setValue('last_day_of_leave', '');
    } else {
        // Clear any existing error messages specific to the field
        g_form.hideFieldMsg('last_day_of_leave');
        g_form.clearMessages();
    }

}
