sys_script_client.script:
function onLoad() {
    // Nom du groupe à rechercher
    var groupName = 'HR';
	var managerName = 'Zainab Oulhcen';

    // Requête GlideRecord pour rechercher le groupe basé sur son nom
    var groupGr = new GlideRecord('sys_user_group');
    groupGr.addQuery('name', groupName);
    groupGr.query();

    // Vérifier si un groupe a été trouvé
    if (groupGr.next()) {
        // Si un groupe est trouvé, récupérer son nom et définir la valeur du champ 'assignment_group'
        var groupName = groupGr.name;
        g_form.setValue('assignment_group', groupName);
    } else {
        // Si aucun groupe n'est trouvé, afficher un message d'erreur ou effectuer une autre action appropriée
        alert("Le groupe de travail '" + groupName + "' n'a pas été trouvé. Veuillez contacter l'administrateur système.");
    }

    // Indiquer à ServiceNow de continuer avec la soumission normale du formulaire
    return true;
}
